def get_max_score(game_state, player):
    """Return the maximum score that can be achieved in the given game state and player.
    >>> get_max_score(game_state, player)
    10
    """
    if game_state.is_over():
        return game_state.utility(player)

    if player == game_state.active_player:
        return max(
            [
                get_max_score(game_state.generate_successor(player, action), player)
                for action in game_state.actions()
            ]
        )

    else:
        return min(
            [
                get_max_score(game_state.generate_successor(player, action), player)
                for action in game_state.actions()
            ]
        )


def minimax(game_state, player, depth=0):
    """Return the minimax value for the given game state and player.
    >>> minimax(game_state, player)
    (1, 1)
    """
    if game_state.is_over():
        return game_state.utility(player)

    if player == game_state.active_player:
        return max(
            [
                minimax(game_state.generate_successor(player, action), player, depth + 1)
                for action in game_state.actions()
            ]
        )

    else:
        return min(
            [
                minimax(game_state.generate_successor(player, action), player, depth + 1)
                for action in game_state.actions()
            ]
        )


